import React, { useEffect, useState, useRef } from 'react';
import socket from '../socket';

function Chat({ roomId, username: currentUser ,playerSymbo}) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket.on("chat_message", ({ username, message, timestamp }) => {
      setMessages(prev => [...prev, { username, message, timestamp }]);
    });

    return () => {
      socket.off("chat_message");
    };
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = () => {
    if (input.trim() === '') return;
    if (playerSymbol === '?') return;

    socket.emit('chat_message', {
      roomId,
      username: currentUser,
      message: input.trim(),
    });

    setInput('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') sendMessage();
  };

  return (
    <div style={styles.container}>
      <h3 style={styles.heading}>Chat</h3>
      <div style={styles.chatBox}>
        {messages.map((msg, i) => {
          const isSelf = msg.username === currentUser;
          return (
            <div
              key={i}
              style={{
                ...styles.messageContainer,
                justifyContent: isSelf ? 'flex-end' : 'flex-start',
              }}
            >
              <div
                style={{
                  ...styles.messageBubble,
                  backgroundColor: isSelf ? '#007bff' : '#28a745',
                  borderTopRightRadius: isSelf ? 0 : 16,
                  borderTopLeftRadius: isSelf ? 16 : 0,
                  alignItems: isSelf ? 'flex-end' : 'flex-start',
                }}
              >
                {/* <strong style={styles.username}>{msg.username}</strong> */}
                <div>{msg.message}</div>
                <div style={styles.timestamp}>
                  {msg.timestamp ? new Date(msg.timestamp).toLocaleTimeString() : ''}
                </div>
              </div>
            </div>
          );
        })}
        <div ref={messagesEndRef} />
      </div>

      <div style={styles.inputArea}>
        <input
          type="text"
          placeholder="Type a message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyPress}
          style={styles.input}
        />
        <button onClick={sendMessage} style={styles.button}>Send</button>
      </div>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: 600,
    width: '95%',
    margin: 'auto',
    border: '1px solid #ccc',
    borderRadius: 10,
    padding: 12,
    display: 'flex',
    flexDirection: 'column',
    height: '80vh',
    backgroundColor: '#f9f9f9',
    boxShadow: '0 0 8px rgba(0,0,0,0.1)',
  },
  heading: {
    margin: 0,
    marginBottom: 10,
    fontWeight: 'bold',
    textAlign: 'center',
    fontSize: '1.5rem',
  },
  chatBox: {
    flexGrow: 1,
    overflowY: 'auto',
    padding: 10,
    marginBottom: 10,
    display: 'flex',
    flexDirection: 'column',
    gap: 10,
  },
  messageContainer: {
    display: 'flex',
  },
  messageBubble: {
    padding: 10,
    borderRadius: 16,
    color: '#fff',
    maxWidth: '75%',
    display: 'flex',
    flexDirection: 'column',
    wordBreak: 'break-word',
  },
  username: {
    fontSize: 12,
    opacity: 0.9,
    marginBottom: 4,
  },
  timestamp: {
    fontSize: 10,
    marginTop: 4,
    opacity: 0.7,
    textAlign: 'right',
  },
  inputArea: {
    display: 'flex',
    gap: 10,
  },
  input: {
    flex: 1,
    padding: 10,
    fontSize: 14,
    borderRadius: 6,
    border: '1px solid #aaa',
  },
  button: {
    padding: '10px 16px',
    fontSize: 14,
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: 6,
    cursor: 'pointer',
  },
};

export default Chat;
